import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Card, Button, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import productsa from '../products';

function ProductScreen({ match }) {
  const productId = match.params._id;
  const pro = productsa.find((p) => p._id === productId);

  if (!pro) {
    // If no product is found with the given _id, you can handle this case, for example:
    return <div>Product not found.</div>;
  }

  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      <h1>{pro.name}</h1>
      <Row>
        <Col md={6}>
          <Image src={pro.image} alt={pro.name} fluid />
        </Col>
        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <Rating value={pro.rating} text={`${pro.numReviews} reviews`} />
            </ListGroup.Item>
            <ListGroup.Item>Price: ${pro.price}</ListGroup.Item>
            <ListGroup.Item>Description: {pro.description}</ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={3}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Row>
                  <Col>Price:</Col>
                  <Col>
                    <strong>${pro.price}</strong>
                  </Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Status:</Col>
                  <Col>
                    {pro.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
                  </Col>
                </Row>
              </ListGroup.Item>

              {pro.countInStock > 0 && (
                <ListGroup.Item>
                  <Button className="btn-block" type="button">
                    Add to Cart
                  </Button>
                </ListGroup.Item>
              )}
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default ProductScreen;
