import React from 'react';

const Product = ({ product }) => {
  const mainStyle = {
    body: {
      minHeight: '100vh',
      background: '#212534',
      display: 'flex',
      alignItems: 'center',
      flexDirection: 'column',
      paddingTop: '2rem',
      paddingBottom: '2rem',
      boxSizing: 'border-box',
    },
    card: {
      background: '#191c29',
      width: 'var(--card-width)',
      height: 'var(--card-height)',
      padding: '3px',
      position: 'relative',
      borderRadius: '6px',
      justifyContent: 'center',
      alignItems: 'center',
      textAlign: 'center',
      display: 'flex',
      fontSize: '1.5em',
      color: 'rgb(88, 199, 250, 0%)',
      cursor: 'pointer',
      fontFamily: 'cursive',
    },
    link: {
      color: '#212534',
      textDecoration: 'none',
      fontFamily: 'sans-serif',
      fontWeight: 'bold',
      marginTop: '2rem',
    },
  };

  return (
    <a href={`/product/${product._id}`}>
      <div style={mainStyle.card} className="card">
        Magic Card
      </div>
      <a href="https://mythrillfiction.com/" target="_blank" style={mainStyle.link}>
        Mythrill
      </a>
    </a>
  );
};

export default Product;
